function getComputerChoice() {
    //pick a rondom number between 0-2
    let Random = Math.floor(Math.random()*3);
    let RPS = ["rock", "paper", "scissors"]
    return RPS[Random];
    //This function will randomly return Rock Paper or Scissors
    //This will be a romdon number generated by computer
}

//fucntion to play the game
function playRound(pSelection, cSlection){
    if(pSelection === cSlection){
        return "Tie Game!";
    } 
    if(pSelection === "rock"){
        if (cSlection === "scissors"){
            return "You Win!";
        } else{
            return "You Lose!";
        }
    } 
    if (pSelection === "paper") {
        if (cSlection === "rock"){
            return "You Win!";
        } else{
            return "You Lose!";
        }
    } 
    if(pSelection === "scissors"){
        if(cSlection === "paper"){
            return  "You Win!";
        } else{
            return "You Lose!";
        }

    }
    return "You didnt pick rock paper or scissors!";
}

