// Java Script Starts here!
// create a variable that will 
//store the players selection

const body = document.querySelector('#body');
const results = document.createElement("div");
results.classList.add('results');


// Best of 5
//Create a new DIV 
//This one will keep score
let scores = [0,0,0];
const score = document.createElement("div");
score.classList.add("score");



const bRock = document.getElementById("bRock");
bRock.addEventListener("click", () => {
    //get computer choice if rock is clicked
    let cSlection = getComputerChoice() ;
    //assing the text content to rock/paper
    results.textContent =  playRound("rock", cSlection);
    scores = game(results.textContent, scores);
    body.append(results);
    score.textContent = scores[0] + " - " + scores[1] + " - " + scores[2];
    body.appendChild(score);
});

const bPaper =document.getElementById("bPaper");
bPaper.addEventListener("click", () => {
    const cSlection = getComputerChoice() ;
    results.textContent = playRound("paper", cSlection);
    scores = game(results.textContent, scores);
    body.appendChild(results);
    score.textContent = scores[0] + " - " + scores[1] + " - " + scores[2];
    body.appendChild(score);
});

const bScissors = document.getElementById("bScissors");

bScissors.addEventListener("click", () => {
    const cSlection = getComputerChoice() ;
    results.textContent = playRound("scissors", cSlection);
    scores = game(results.textContent, scores);
    body.appendChild(results);
    score.textContent = scores[0] + " - " + scores[1] + " - " + scores[2];
    body.appendChild(score);
});



